import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import ArticleCard from './ArticleCard';

const Profile: React.FC = () => {
  const { user } = useAuth();

  if (!user) {
    return <div className="loading">Loading profile...</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2 className="profile-name">Welcome, {user.name}!</h2>
        <p className="profile-email">{user.email}</p>
      </div>

      <div>
        <h3 className="saved-articles-title">
          Your Saved Articles ({user.savedArticles.length})
        </h3>
        
        {user.savedArticles.length === 0 ? (
          <div className="no-articles">
            <p>You haven't saved any articles yet.</p>
            <p>Search for news and click "Save" to add articles to your profile!</p>
          </div>
        ) : (
          <div className="articles-grid">
            {user.savedArticles.map((article, index) => (
              <ArticleCard key={`${article.url}-${index}`} article={article} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
